angular.module("st2forget.action-verbs-game",[]).directive("actionVerbsGame",function(){var e={description:"Practice Action Verbs Vocabulary with this ESL  Memory Game. This game is also excellent for Vocabulary Teaching and Practice. ESL Learners and Teachers can use it to review English vocabulary or simply practice these words. This memory games has audio, images and text which makes it possible to practice spelling, reading, listening and word recognition. It is great for beginner and elementary ESL Lesson Plans.",questions:[{id:1,statement:"Drink",answers:[{id:1,value:"Drink"},{id:2,value:"Uong"}]},{id:2,statement:"Swim",answers:[{id:3,value:"Swim"},{id:4,value:"Boi"}]},{id:3,statement:"Eat",answers:[{id:5,value:"Eat"},{id:6,value:"An"}]},{id:4,statement:"Run",answers:[{id:7,value:"Run"},{id:8,value:"Chay"}]}]},t=function(e,t,i){};return{controllerAs:"actionVerbsGame",controller:["$attrs","$scope","$element",function(t,i,a){i.directiveRootPath=t.directiveRootPath,i.model=e,i.buildModel=function(){var e=[],t=i.model.questions;return t.forEach(function(t){t.answers.forEach(function(i){i.questionId=t.id,e.push(i)})}),e=_.shuffle(e)},i.sortedAnswers=i.buildModel(),i.getTemplateUrl=function(){return i.directiveRootPath+"/angular-action-verbs-game/templates/action-verbs.html"},i.itemClick=function(e){e.isActive=!e.isActive}}],template:'<ng-include src="getTemplateUrl()"></ng-include>',link:t}});